// react components are named with PascalCase
// rule -> having only one root element

import './ExpenseItem.css';
// then we need to add the classes (className instead of class)

function ExpenseItem() {

    const expenseDate = new Date(2021, 2, 28);  // date object
    const expeseTitle = prompt("expense title: ");  // simple string
    const expenseAmount = prompt("expense price: ")  // simple float number

    return (<div className='expense-item'><div>{expenseDate}</div>
        <div className='expense-item__description'>
            <h2>{expeseTitle}</h2>
            <div className='expense-item__price'>{'$' + expenseAmount}</div>
        </div></div>)
}

export default ExpenseItem;
